import { useEffect, useState } from 'react';
import MoviesContainer from './MoviesContainer';
import SearchBox from './SearchBox';
import { useMovies } from '../hooks/useMovies';

// import M from 'materialize-css';
import M from 'materialize-css/dist/js/materialize.min.js';

// initializing materialize autofill input
// const autoInput = M.Autocomplete.init();
// M.Autocomplete.init();

const apiKey = process.env.REACT_APP_API_KEY;
const options2 = {
  '247°F': null,
  'B.F.F.': null,
  'Best F(r)iends: Volume 1': null,
  'Best F(r)iends: Volume 2': null,
  'Christiane F. - Wir Kinder vom Bahnhof Zoo': null,
  F: null,
  'F for Franco': null,
  'F#Ck Nick Cannon': null,
  'F*&% the Prom': null,
  'F/X': null,
  'F/X2': null,
  Fandango: null,
  "I'm Just F*cking with You": null,
  'The Death & Life of John F. Donovan': null,
  'The F Word': null,
  'The F**k-It List': null,
  'The Other F Word': null,
  'U.F.O.': null,
  'ドラゴンボールZ 復活の「F」': null,
  'ドラゴンボールZ 復活の「F」 “未来”トランクス特別編': null
};

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const { isLoading, movies } = useMovies(searchTerm);
  const [inputOptions, setInputOptions] = useState(null);

  const autoInput = document.getElementById('autocomplete-input');

  // building input options object
  useEffect(() => {
    // M.AutoInit();
    searchTerm &&
      fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchTerm}&include_adult=false`
      )
        .then(res => {
          return res.json();
        })
        .then(data => {
          console.log('data :>> ', data.results);
          let options = {};
          data.results.map(movie => {
            return (options[`${movie.original_title}`] = null);
          });
          console.log('options :>> ', options);
          setInputOptions(options);
          return options;
        });
    M.Autocomplete.init(autoInput, { data: options2 });
    debugger;
  }, [searchTerm]);

  const onSearchInputChange = input => {
    setSearchTerm(input);
    // getInputOptions(movies);
  };

  // displaying options
  // autoInput.updateData(getInputOptions(movies));

  let moviesDom;
  if (isLoading) {
    moviesDom = <div>Loading...</div>;
  } else {
    moviesDom = <MoviesContainer movies={movies} />;
  }

  return (
    <div>
      <SearchBox
        searchTerm={searchTerm}
        onSearchInputChange={onSearchInputChange}
      />
      {/* <MoviesContainer movies={searchOutput} /> */}
      {moviesDom}
    </div>
  );
};

export default Home;
