import { useState } from 'react';
import { Link } from 'react-router-dom';
import ChooseList from './ChooseList';
import M from 'materialize-css';

import film from '../assets/film.svg';
const MovieCard = ({ movie, key }) => {
  const [showLists, setShowLists] = useState([]);
  M.AutoInit();
  // M.Modal.init({ dismissible: false });

  // const addMovie = () => {};
  return (
    <div key={key} className='col s12 m4 l3'>
      <div className='card blue-grey darken-2'>
        <div className='card-image waves-block waves-light'>
          {movie.poster_path ? (
            <img
              className='activator'
              src={`https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}
            />
          ) : (
            <img src={film} className='' />
          )}
          <i
            className='far fa-bookmark modal-trigger'
            href='#lists-modal'
            // onClick={addMovie}
          ></i>

          <div id='lists-modal' className='modal blue-grey'>
            <div className='modal-content'>
              <h4>My Lists</h4>
              {!showLists && (
                <>
                  <p>You have no lists yet. Wanna create?</p>
                  <a className='waves-effect waves-light btn'>Create</a>
                  <a className='waves-effect waves-light btn'>Cancel</a>
                </>
              )}
            </div>
            <div className='modal-footer'>
              <a
                href='#!'
                className='modal-close waves-effect waves-green btn-flat'
              >
                Agree
              </a>
            </div>
          </div>
        </div>
        <div className='card-reveal'>
          <span className='card-title'>
            <i className='fas fa-chevron-down right'></i>
            {movie.original_title}
          </span>

          <p>
            Ratings: {movie.vote_average} <i className='fas fa-star '></i>
          </p>
          <p>Genre: Drama</p>
          <p>Year: {movie.release_date}</p>
          <p>
            <a
              href={`https://www.themoviedb.org/movie/${movie.id}`}
              target='_blank'
            >
              Read More
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
