import { useState } from 'react';
import film from '../assets/film.svg';
import M from 'materialize-css';

const Bookmarked = () => {
  M.AutoInit();
  // M.Collapsible.init();

  const [saved, setSaved] = useState([
    {
      id: 761053,
      original_title: "Gabriel's Inferno Part II",
      vote_average: 8.8,
      overview:
        "Professor Gabriel Emerson finally learns the truth about Julia Mitchell's identity, but his realization comes a moment too late. Julia is done waiting for the well-respected Dante specialist to remember her and wants nothing more to do with him. Can Gabriel win back her heart before she finds love in another's arms?",
      poster_path: '/fYtHxTxlhzD4QWfEbrC1rypysSD.jpg'
    },
    {
      original_title: "Gabriel's Inferno Part II",
      vote_average: 8.8,
      overview:
        "Professor Gabriel Emerson finally learns the truth about Julia Mitchell's identity, but his realization comes a moment too late. Julia is done waiting for the well-respected Dante specialist to remember her and wants nothing more to do with him. Can Gabriel win back her heart before she finds love in another's arms?",
      poster_path: '/fYtHxTxlhzD4QWfEbrC1rypysSD.jpg'
    }
  ]);

  return (
    <div className='container'>
      <div className='header'>
        <h2>Bookmarked</h2>
      </div>
      <ul class='collapsible popout'>
        {saved.map((movie, index) => {
          return (
            <li>
              <div className='collapsible-header blue-grey darken-2'>
                <div>
                  <i className='fas fa-film'> </i>
                  {index} {movie.original_title}
                </div>{' '}
                <div>
                  Ratings: {movie.vote_average} <i className='fas fa-star '></i>
                </div>
              </div>
              <div className='collapsible-body row'>
                {
                  <div className='bookmarked-image col s2'>
                    {movie.poster_path ? (
                      <img
                        className='activator'
                        src={`https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}
                      />
                    ) : (
                      <img src={film} className='' />
                    )}
                  </div>
                }{' '}
                <span className='col s10'>{movie.overview}</span>
                <a
                  href={`https://www.themoviedb.org/movie/${movie.id}`}
                  target='_blank'
                >
                  Read More
                </a>
              </div>
            </li>
          );
        })}
      </ul>
    </div>

    // <div className='container'>
    //   <ul class='collapsible'>
    //     <li>
    //       <div class='collapsible-header'>
    //         <i class='material-icons'>filter_drama</i>First
    //       </div>
    //       <div class='collapsible-body'>
    //         <span>Lorem ipsum dolor sit amet.</span>
    //       </div>
    //     </li>
    //     <li>
    //       <div class='collapsible-header'>
    //         <i class='material-icons'>place</i>Second
    //       </div>
    //       <div class='collapsible-body'>
    //         <span>Lorem ipsum dolor sit amet.</span>
    //       </div>
    //     </li>
    //     <li>
    //       <div class='collapsible-header'>
    //         <i class='material-icons'>whatshot</i>Third
    //       </div>
    //       <div class='collapsible-body'>
    //         <span>Lorem ipsum dolor sit amet.</span>
    //       </div>
    //     </li>
    //   </ul>
    // </div>
  );
};

export default Bookmarked;
