import film from '../assets/film.svg';
const MoviesContainer = ({ movies }) => {
  return (
    // <div className='container'>
    //   <div className='row'>
    //     <div className='col s12 m4 l4'>
    //       <div className='card blue-grey darken-2'>
    //         <div className='card-image'>
    //           <a className='bookmark-btn'>
    //             <i className='far fa-bookmark'></i>
    //           </a>
    //           <img src='https://www.themoviedb.org/t/p/w600_and_h900_bestv2/q6y0Go1tsGEsmtFryDOJo3dEmqu.jpg' />
    //           <span className='card-title'>Card Title</span>
    //         </div>
    //         <div className='card-content left-align'>
    //           <p>
    //             Ratings: 9.9 <i className='fas fa-star '></i>
    //           </p>
    //           <p>Genre: Drama</p>
    //           <p>Year: 2012-12-20</p>

    //           <a href=''>Read More</a>
    //         </div>
    //       </div>
    //     </div>
    //   </div>
    // </div>
    <div className='container'>
      <div className='row'>
        {movies.map(movie => {
          return (
            <div key={movie.id} className='col s12 m4 l3'>
              <div className='card blue-grey darken-2'>
                <div className='card-image'>
                  <a className='bookmark-btn'>
                    <i className='far fa-bookmark'></i>
                  </a>
                  {/* <img
                    src={movie.poster_path ? {`https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}: {'../../public/film-solid.gif'}}
                    alt='Poster image'
                  /> */}

                  {movie.poster_path ? (
                    <img
                      src={`https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}
                    />
                  ) : (
                    <img src={film} className='' />
                  )}
                </div>
                <div className='card-content left-align'>
                  <span className='card-title'>{movie.original_title}</span>
                  <p>
                    Ratings: {movie.vote_average}{' '}
                    <i className='fas fa-star '></i>
                  </p>
                  <p>Genre: Drama</p>
                  <p>Year: {movie.release_date}</p>

                  <a
                    href={`https://www.themoviedb.org/movie/${movie.id}`}
                    target='_blank'
                  >
                    Read More
                  </a>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MoviesContainer;
