import { useRef, useState } from 'react';

import film from '../assets/film.svg';
const MovieCard = ({ movie, key }) => {
  const [addIcon, setAddIcon] = useState(false);
  // const [isHidden, setIsHidden] = useState(false);

  const cardRef = useRef(null);

  const handleMouseEnter = () => {
    let overflow = cardRef.current.style.overflow;
    overflow === 'visible' ? setAddIcon(true) : addIcon;
  };

  return (
    <div key={key} className='col s12 m4 l3'>
      <div
        className='card blue-grey darken-2'
        ref={cardRef}
        onMouseEnter={handleMouseEnter}
      >
        <div
          className='card-image waves-effect waves-block waves-light'

          // onMouseEnter={e => {
          //   {
          //     (e.target.style = { overflow: 'visible' })
          //       ? setAddIcon(true)
          //       : addIcon;
          //   }
          // }}
          // onMouseLeave={() => {
          //   setAddIcon(false);
          // }}
        >
          {movie.poster_path ? (
            <img
              className='activator'
              src={`https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movie.poster_path}`}
            />
          ) : (
            <img src={film} className='' />
          )}
          {addIcon && <i className='far fa-bookmark'></i>}
        </div>
        <div className='card-reveal'>
          <span className='card-title'>
            <i className='fas fa-chevron-down right'></i>
            {movie.original_title}
          </span>

          <p>
            Ratings: {movie.vote_average} <i className='fas fa-star '></i>
          </p>
          <p>Genre: Drama</p>
          <p>Year: {movie.release_date}</p>
          <p>
            <a
              href={`https://www.themoviedb.org/movie/${movie.id}`}
              target='_blank'
            >
              Read More
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
