import { useEffect, useState } from 'react';
import MoviesContainer from './MoviesContainer';
import SearchBox from './SearchBox';
import { useMovies } from '../hooks/useMovies';
import useFetch from '../hooks/useFetch';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');

  // const { isLoading, movies } = useMovies(searchTerm);

  const onSearchInputChange = input => {
    const apiKey = process.env.REACT_APP_API_KEY;
    const apiEndPoint = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchTerm}&include_adult=false`;
    setSearchTerm(input);
    const moviesOnChange = useFetch(apiEndPoint);
    console.log('moviesOnChange: ', moviesOnChange);
  };

  return (
    <div>
      <SearchBox
        searchTerm={searchTerm}
        onSearchInputChange={onSearchInputChange}
      />
    </div>
  );
};

export default Home;
