import { useEffect, useState } from 'react';
import MoviesContainer from './MoviesContainer';
import SearchBox from './SearchBox';
import { useMovies } from '../hooks/useMovies';
import M from 'materialize-css';

// initializing materialize autofill input
M.AutoInit();
const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const { isLoading, movies } = useMovies(searchTerm);
  const [inputOptions, setInputOptions] = useState(null);

  function getInputOptions(movies) {
    let options = {};
    movies.map(movie => {
      return (options[`${movie.movieData.original_title}`] = null);
    });
    console.log('options :>> ', options);
    return options;
  }

  const onSearchInputChange = input => {
    setSearchTerm(input);
    getInputOptions(movies);
  };

  let moviesDom;
  if (isLoading) {
    moviesDom = <div>Loading...</div>;
  } else {
    moviesDom = <MoviesContainer movies={movies} />;
  }

  return (
    <div>
      <SearchBox
        searchTerm={searchTerm}
        onSearchInputChange={onSearchInputChange}
      />
      {/* <MoviesContainer movies={searchOutput} /> */}
      {moviesDom}
    </div>
  );
};

export default Home;
