import { useEffect, useState } from 'react';
import useFetch from './useFetch';
const apiKey = process.env.REACT_APP_API_KEY;
const apiEndPoint = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchTerm}&include_adult=false`;

// const fetchMovieData = async (movie)=> {
//   const res = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchTerm}&include_adult=false`)
// }

export const useMovies = searchTerm => {
  const [movies, setMovies] = useState([]);

  fetch(apiEndPoint)
    .then(res => {
      return res.json();
    })
    .then(data => {
      console.log('Got movies list from API :>> ', data.results);
      return data.results;
    });

  // return {
