import { useEffect, useState } from 'react';
import MoviesContainer from './MoviesContainer';
import SearchBox from './SearchBox';
import { useMovies } from '../hooks/useMovies';

import M from 'materialize-css';

// initializing materialize autofill input
// const autoInput = M.Autocomplete.init();

const apiKey = process.env.REACT_APP_API_KEY;
M.Autocomplete.init({ SearchBox }, { data: opts });
const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const { isLoading, movies } = useMovies(searchTerm);
  const [inputOptions, setInputOptions] = useState(null);

  // building input options object
  useEffect(() => {
    searchTerm &&
      fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchTerm}&include_adult=false`
      )
        .then(res => {
          return res.json();
        })
        .then(data => {
          console.log('data :>> ', data.results);
          let options = {};
          data.results.map(movie => {
            return (options[`${movie.original_title}`] = null);
          });
          console.log('options :>> ', options);
          setInputOptions(options);
          return options;
        });
  }, [searchTerm]);

  const onSearchInputChange = input => {
    setSearchTerm(input);
    // getInputOptions(movies);
  };

  // displaying options
  // autoInput.updateData(getInputOptions(movies));

  let moviesDom;
  if (isLoading) {
    moviesDom = <div>Loading...</div>;
  } else {
    moviesDom = <MoviesContainer movies={movies} />;
  }

  return (
    <div>
      <SearchBox
        searchTerm={searchTerm}
        onSearchInputChange={onSearchInputChange}
      />
      {/* <MoviesContainer movies={searchOutput} /> */}
      {moviesDom}
    </div>
  );
};

export default Home;
