import { useState, useEffect } from 'react';
import _testData from '../_testData.json';
import MoviesContainer from './MoviesContainer';
import useTop250 from '../hooks/useTop250';

const apiEndPoint = process.env.REACT_APP_IMDB_TOP250_END_POINT;

const Top250 = () => {
  const { isLoading, movies } = useTop250();

  useEffect(() => {
    const getTop = async () => {
      const top250Movies = await fetchTop250();
      setTop250(top250Movies);
    };
    getTop();
    console.log('top250 :>> ', top250);
  }, []);

  // Fetch Top250 movies
  const fetchTop250 = async () => {
    const res = await fetch(apiEndPoint);
    const data = await res.json();
    console.log('data.results :>> ', data.results);
    return data.results;
  };

  const fetchMovieData = async movie => {
    // console.log('in fetch movie data');
    const res = await fetch(apiEndPoint);
    const data = await res.json();
    console.log('data:', data);
    return data;
  };

  return <MoviesContainer movies={_testData.results} />;
};

export default Top250;
